var prefix = '';
exports.default = {
  setPrefix: function setPrefix(val) {
    prefix = val;
  },
  /**
  * @description: localStorage存储封装
  * @param {String} name
  * @param {String} value
  * @return: void
  */
  set: function set(name, value) {
    localStorage.setItem(prefix + name, value);
  },

  /**
   * @description: localStorage获取封装
   * @param {String} name
   * @return: String
   */
  get: function get(name) {
    return localStorage.getItem(prefix + name);
  },

  /**
   * @description: localStorage移除封装
   * @param {String} name
   * @param {String} value
   * @return: void
   */
  remove: function remove(name, value) {
    localStorage.removeItem(prefix + name, value);
  },

  /**
   * @description: localStorage清空封装
   * @param {String} name
   * @return: String
   */
  clear: function clear() {
    var len = localStorage.length;
    var keys = [];

    for (var i = 0; i < len; i++) {
      var key = localStorage.key(i);
      if (key && key.startsWith(prefix)) {
        keys.push(key);
      }
    }

    keys.map(function (key) {
      return localStorage.removeItem(key);
    });
  }
};