import { isNumber } from '@fawkes-lib/src/utils/type'

export const isValidWidthUnit = (val) => {
  if (isNumber(val)) {
    return true
  } else {
    return ['px', 'rem', 'em', 'vw', '%', 'vmin', 'vmax'].some(unit =>
      val.endsWith(unit),
    )
  }
  return false
}


export const isValidComponentSize = (val) =>
  ['', 'large', 'medium', 'small', 'mini'].includes(val)
